import{_ as e,c as a,o as t,a2 as n}from"./chunks/framework.o7xlcM00.js";const k=JSON.parse('{"title":"EAI-Service","description":"","frontmatter":{},"headers":[],"relativePath":"features/eai-service/index.md","filePath":"features/eai-service/index.md"}'),i={name:"features/eai-service/index.md"},r=n('<h1 id="eai-service" tabindex="-1">EAI-Service <a class="header-anchor" href="#eai-service" aria-label="Permalink to &quot;EAI-Service&quot;">​</a></h1><p>Das Wahllokalsystem hängt von anderen Anwendungen ab, an die es Daten sendet und von den es Daten importiert. Die Schnittstelle zwischen diesen Anwendungen wird über diesen EAI-Service realisiert.</p><h2 id="abhangigkeiten" tabindex="-1">Abhängigkeiten <a class="header-anchor" href="#abhangigkeiten" aria-label="Permalink to &quot;Abhängigkeiten&quot;">​</a></h2><p>Der Service hat keine Abhängigkeiten zu anderen Services.</p><h2 id="daten-und-funktionen" tabindex="-1">Daten und Funktionen <a class="header-anchor" href="#daten-und-funktionen" aria-label="Permalink to &quot;Daten und Funktionen&quot;">​</a></h2><h3 id="wahldaten" tabindex="-1">Wahldaten <a class="header-anchor" href="#wahldaten" aria-label="Permalink to &quot;Wahldaten&quot;">​</a></h3><p>Es können Informationen zu den Wahltagen, Wahlbezirken, Wahlen, Wahlbasisdaten und Wahlberechtigten abgerufen werden.</p><h3 id="wahlvorschlage" tabindex="-1">Wahlvorschläge <a class="header-anchor" href="#wahlvorschlage" aria-label="Permalink to &quot;Wahlvorschläge&quot;">​</a></h3><p>Es können die Wahlvorschläge und Referendumsvorschläge geladen werden.</p><h3 id="wahlvorstandsdaten" tabindex="-1">Wahlvorstandsdaten <a class="header-anchor" href="#wahlvorstandsdaten" aria-label="Permalink to &quot;Wahlvorstandsdaten&quot;">​</a></h3><p>Es können die Wahlvorstände für die Wahlbezirke geladen werden sowie deren Anwesenheit mitgeteilt werden.</p><h3 id="wahlergebnisse" tabindex="-1">Wahlergebnisse <a class="header-anchor" href="#wahlergebnisse" aria-label="Permalink to &quot;Wahlergebnisse&quot;">​</a></h3><p>Das Ergebnis das in den Wahllokalen ermittelt wurde kann mitgeteilt werden.</p><h3 id="wahlbeteiligung" tabindex="-1">Wahlbeteiligung <a class="header-anchor" href="#wahlbeteiligung" aria-label="Permalink to &quot;Wahlbeteiligung&quot;">​</a></h3><p>Wahlbeteiligungen der Wahllokale kann mitgeteilt werden.</p><h3 id="wahllokalstatus" tabindex="-1">Wahllokalstatus <a class="header-anchor" href="#wahllokalstatus" aria-label="Permalink to &quot;Wahllokalstatus&quot;">​</a></h3><p>Der Zustand in dem sich die Wahllokale befinden kann mitgeteilt werden.</p><h2 id="zugriffsbeschrankungen" tabindex="-1">Zugriffsbeschränkungen <a class="header-anchor" href="#zugriffsbeschrankungen" aria-label="Permalink to &quot;Zugriffsbeschränkungen&quot;">​</a></h2><p>Übersicht über die Endpunkte und die dafür benötigten Rechte:</p><table tabindex="0"><thead><tr><th>Endpunkt</th><th>erforderliche Rechte</th></tr></thead><tbody><tr><td>GET /wahldaten/wahlbezirke/{wahlbezirkID}/wahlberechtigte</td><td>aoueai_BUSINESSACTION_LoadWahlberechtigte</td></tr><tr><td>GET /wahldaten/wahltage?includingSince</td><td>aoueai_BUSINESSACTION_LoadWahltage</td></tr><tr><td>GET /wahldaten/wahlbezirk?forDate&amp;withNummer</td><td>aoueai_BUSINESSACTION_LoadWahlbezirke</td></tr><tr><td>GET /wahldaten/wahlen?forDate&amp;withNummer</td><td>aoueai_BUSINESSACTION_LoadWahlen</td></tr><tr><td>GET /wahldaten/basisdaten?forDate&amp;withNummer</td><td>aoueai_BUSINESSACTION_LoadBasisdaten</td></tr></tbody></table>',20),d=[r];function h(l,s,o,u,c,b){return t(),a("div",null,d)}const f=e(i,[["render",h]]);export{k as __pageData,f as default};
